CREATE TABLE IF NOT EXISTS MEMBER_DATA(id INT NOT NULL AUTO_INCREMENT, USER_ACCOUNT VARCHAR(50), BIRTH_DAY VARCHAR(255), USER_NAME VARCHAR(255), PRIMARY KEY (id));
CREATE TABLE IF NOT EXISTS PRODUCT_ITEM(id INT NOT NULL AUTO_INCREMENT, PRODUCT_ID CHAR(10), PRODUCT_NAME VARCHAR(255), PRODUCT_PRICE INT, PRIMARY KEY (id));
CREATE TABLE IF NOT EXISTS BOUGHT_ITEM(id INT NOT NULL AUTO_INCREMENT, ORDER_ID CHAR(10), PRODUCT_ID CHAR(10), USER_ACCOUNT VARCHAR(50), BUY_DATE DATE, PRIMARY KEY (id));